    assert isinstance(name, str), "name must be a string"

    def unquoted_char(match):
        """For each ;000 return the corresponding byte."""
        if len(match.group()) != 4:
            return match.group()
        try:
            return bytes([int(match.group()[1:])]).decode('latin-1')
        except ValueError:
            return match.group()
