from typing import Dict
import json
from lxml import etree

def parse_diaspora_webfinger(document: str) -> Dict:
    try:
        # Attempt to parse as JSON
        data = json.loads(document)
        hcard_url = find_link(data, 'hcard')
    except json.JSONDecodeError:
        # If JSON parsing fails, attempt to parse as XRD
        root = etree.fromstring(document)
        data = parse_xrd(root)
        hcard_url = find_link(data, 'hcard')

    return {
        'hcard_url': hcard_url
    }

def find_link(data, rel):
    for link in data.get('links', []):
        if link.get('rel') == rel:
            return link.get('href')
    logger.warning(f"No link found with rel '{rel}'")
    return None

def parse_xrd(root):
    # This function should parse the XRD document and return a dictionary
    # similar to the JSON structure. This is a simplified version.
    data = {
        'links': []
    }
    for link in root.findall('.//{http://docs.oasis-open.org/ns/xri/xrd-1.0}Link'):
        rel = link.get('rel')
        href = link.get('href')
        if rel and href:
            data['links'].append({'rel': rel, 'href': href})
    return data

