def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        if isinstance(node, dict):
            for key, value in node.items():
                if key in result and isinstance(result[key], dict) and isinstance(value, dict):
                    result[key] = deep_merge_nodes([result[key], value])
                else:
                    result[key] = value
    return result

from ruamel.yaml.nodes import ScalarNode, MappingNode

def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        if isinstance(node, tuple) and len(node) == 2 and isinstance(node[0], ScalarNode):
            key, value = node
            if key.value in result:
                if isinstance(result[key.value], MappingNode) and isinstance(value, MappingNode):
                    result[key.value] = deep_merge_nodes([result[key.value], value])
                else:
                    result[key.value] = value
            else:
                result[key.value] = value
    # Convert the result back to the desired format
    return [(ScalarNode(tag=key.tag, value=key.value), value) for key, value in result.items()]

