    # Remove unused argument 'cls'
    def get_protocol_handlers(protocol_version=None):
        handlers = {}
        if protocol_version is None:
            handlers[(1, 0)] = BoltProtocolHandlerV1
            handlers[(2, 0)] = BoltProtocolHandlerV2
            handlers[(3, 0)] = BoltProtocolHandlerV3
        else:
            if not isinstance(protocol_version, tuple):
                raise TypeError("Protocol version must be a tuple")
            if protocol_version == (1, 0):
                handlers[protocol_version] = BoltProtocolHandlerV1
            elif protocol_version == (2, 0):
                handlers[protocol_version] = BoltProtocolHandlerV2
            elif protocol_version == (3, 0):
                handlers[protocol_version] = BoltProtocolHandlerV3
            else:
                raise ValueError("Invalid protocol version")
        return handlers

