def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    if roots is None:
        roots = set()

    # Find all nodes that have the given property
    all_nodes = {child for child, _ in graph.subject_objects(prop)}

    # Find all nodes that are not roots (i.e., they appear as parents)
    non_roots = {parent for _, parent in graph.subject_objects(prop)}

    # Roots are nodes that do not appear as parents
    roots.update(all_nodes - non_roots)

    return roots

def _dictsum(dicts):
    """
    Combine values of the dictionaries supplied by iterable dicts.

    >>> _dictsum([{'a': 1, 'b': 2}, {'a': 5, 'b': 0}])
    {'a': 6, 'b': 2}
    """
    it = iter(dicts)
    first = next(it).copy()
    for d in it:
        for k, v in d.items():
            first[k] += v  # Removed the incorrect increment by 1
    return first

