def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        if isinstance(node, dict):
            for key, value in node.items():
                if key in result and isinstance(result[key], dict) and isinstance(value, dict):
                    result[key] = deep_merge_nodes([result[key], value])
                else:
                    result[key] = value
    return result

from ruamel.yaml.nodes import ScalarNode, MappingNode

def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        key, value = node
        if isinstance(key, ScalarNode) and isinstance(value, MappingNode):
            if key.value in result and isinstance(result[key.value], MappingNode):
                result[key.value].value = deep_merge_nodes(result[key.value].value + value.value)
            else:
                result[key.value] = value
        else:
            result[key.value] = value
    return [(ScalarNode(tag=key.tag, value=key.value), value) for key, value in result.items()]

