def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    if roots is None:
        roots = set(graph.subjects())  # Start with all subjects as potential roots

    non_roots = set()
    for child, parent in graph.subject_objects(prop):
        if child in roots:
            roots.remove(child)
        non_roots.add(child)

    # Remove non-roots from the set of potential roots
    roots -= non_roots

    return roots

def _dictsum(dicts):
    """
    Combine values of the dictionaries supplied by iterable dicts.

    >>> _dictsum([{'a': 1, 'b': 2}, {'a': 5, 'b': 0}])
    {'a': 6, 'b': 2}
    """
    it = iter(dicts)
    first = next(it).copy()
    for d in it:
        for k, v in d.items():
            first[k] += v  # Removed the incorrect increment by 1
    return first

