def force_string(obj: Union[str, bytes, numpy.bytes_]) -> bytes:
    """
    This function returns the bytes object corresponding to ``obj``.
    If ``obj`` is a string, it encodes it to bytes using UTF-8.
    If ``obj`` is already a bytes object, it returns it as is.
    If ``obj`` is a numpy.bytes_ object, it converts it to a standard bytes object.
    Raises a TypeError if the input type is not supported.
    """
    if isinstance(obj, str):
        return obj.encode('utf-8')
    elif isinstance(obj, numpy.bytes_):
        return bytes(obj)
    elif isinstance(obj, bytes):
        return obj
    else:
        raise TypeError("Unsupported type. Expected str, bytes, or numpy.bytes_.")

