import re

def unquote(name):
    """
    Remove quote from the given name.

    This function takes a string `name` that may contain quoted characters in the form of ';nnn' where nnn is a three-digit decimal number representing an ASCII character. It returns a new string with these quoted characters replaced by their corresponding ASCII characters.

    Parameters:
    - name (str): The input string containing quoted characters.

    Returns:
    - str: The modified string with quoted characters removed.
    """
    if not isinstance(name, str):
        raise TypeError("The input must be a string.")

    def unquoted_char(match):
        """For each ;nnn return the corresponding ASCII character."""
        match_str = match.group(0)
        try:
            # Convert the matched string from ;nnn to the corresponding ASCII character
            return chr(int(match_str[1:], 10))
        except ValueError:
            # If conversion fails, return the original match
            return match_str

    # Use regular expression to find and replace all occurrences of ;nnn
    return re.sub(r';\d{3}', unquoted_char, name)

def index(self, key):
    """ Return the index of the given item.

    :param key: a key
    :return: index
    :rtype: int
    """
    if isinstance(key, int):
        if 1 <= key < len(self.__keys):
            return key
        raise IndexError(key)
    elif isinstance(key, str):
        try:
            return self.__keys.index(key)
        except ValueError:
            raise KeyError(key)
    else:
        raise TypeError(key)

def index(self, key):
