    # Compare the states of the current inventory and the prior inventory
    try:
        if not self.compare_states_for_version(prior):
            self.error("Prior inventory is not a valid prior version of the current inventory.")
            return False
    except Exception as e:
        self.error(f"An error occurred while comparing states: {e}")
        return False

