def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    if padding is not None:
        if padding < 1:
            raise ValueError("Padding must be a positive integer")
        # Adjust number to ensure correct padding
        number += (len(alphabet) ** padding) - 1

    if number < 0:
        raise ValueError("Number must be a non-negative integer")

    output = []
    while number:
        number, digit = divmod(number, len(alphabet))
        output.append(alphabet[digit])

    if padding is not None:
        # Ensure the output is at least of length padding
        output.extend([alphabet[0]] * max(0, padding - len(output)))

    return ''.join(reversed(output))

from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    if padding is not None:
        if padding < 1:
            raise ValueError("Padding must be a positive integer")

    if number < 0:
        raise ValueError("Number must be a non-negative integer")

    # Handle the case where number is zero
    if number == 0:
        return alphabet[0] * (padding if padding is not None else 1)

    output = []
    while number:
        number, digit = divmod(number, len(alphabet))
        output.append(alphabet[digit])

    if padding is not None:
        # Ensure the output is at least of length padding
        output.extend([alphabet[0]] * max(0, padding - len(output)))

    return ''.join(reversed(output))

