def popitem(self):
    """Remove and return the `(key, value)` pair least frequently used."""
    try:
        # Get the least frequently used item by reversing the order of most_common
        (key, _), = self.__counter.most_common()[-1:]
    except IndexError:
        raise KeyError('The container is empty.') from None
    else:
        value = self.pop(key)
        return (key, value)

