import socket
import re

def is_local(host):
    """
    Checks if the host is the localhost

    :param host: The hostname or ip
    :return: True if the host is the localhost
    """
    if not isinstance(host, str):
        raise ValueError("The host parameter must be a string.")

    # Consolidate all local identifiers into a single set for efficient membership testing
    local_identifiers = {'localhost', '127.0.0.1', '::1', 'localhost.localdomain'}

    # Regular expressions for matching private IP address ranges
    private_ip_ranges = [
        r'^10\.\d{1,3}\.\d{1,3}\.\d{1,3}$',  # 10.0.0.0 - 10.255.255.255
        r'^172\.(1[6-9]|2[0-9]|3[0-1])\.\d{1,3}\.\d{1,3}$',  # 172.16.0.0 - 172.31.255.255
        r'^192\.168\.\d{1,3}\.\d{1,3}$'  # 192.168.0.0 - 192.168.255.255
    ]

    # Check if the host is in the local identifiers set
    if host in local_identifiers:
        return True

    # Check if the host is a valid IP address
    if re.match(r'^(\d{1,3}\.){3}\d{1,3}$', host):
        # Check if the IP address is in one of the private IP ranges
        for pattern in private_ip_ranges:
            if re.match(pattern, host):
                return True
        return False

    # Use socket to resolve the host and check if it maps to a local IP
    try:
        host_info = socket.gethostbyaddr(host)
        host_ip = host_info[2][0]  # Get the first IP address associated with the host
        if host_ip in local_identifiers:
            return True
        # Check if the resolved IP is in one of the private IP ranges
        for pattern in private_ip_ranges:
            if re.match(pattern, host_ip):
                return True
    except socket.herror:
        # If the host cannot be resolved, return False
        return False
    except Exception as e:
        # Catch other exceptions and provide more informative feedback
        print(f"An error occurred while resolving the host: {e}")
        return False

