def deep_merge_nodes(nodes):
    result = {}

    for node in nodes:
        if isinstance(node, tuple) and len(node) == 2:
            key, value = node
            if isinstance(key, ScalarNode) and isinstance(value, (MappingNode, ScalarNode)):
                key_value = key.value
                if key_value in result:
                    if isinstance(result[key_value], MappingNode) and isinstance(value, MappingNode):
                        # Merge two MappingNodes
                        merged_value = deep_merge_nodes(list(zip(*value.value)))
                        result[key_value].value = merged_value
                    else:
                        # Non-MappingNode value, the last one wins
                        result[key_value] = value
                else:
                    result[key_value] = value
    return [(ScalarNode(tag=key.tag, value=key.value), value) for key, value in result.items()]

