from ruamel.yaml.nodes import ScalarNode, MappingNode

def deep_merge_nodes(nodes):
    """
    Given a nested borgmatic configuration data structure as a list of tuples in the form of:

    (
        ruamel.yaml.nodes.ScalarNode as a key,
        ruamel.yaml.nodes.MappingNode or other Node as a value,
    ),

    ... deep merge any node values corresponding to duplicate keys and return the result. If
    there are colliding keys with non-MappingNode values (e.g., integers or strings), the last
    of the values wins.
    """
    result = {}

    for node in nodes:
        if not (isinstance(node, tuple) and len(node) == 2):
            continue

        key, value = node
        if not (isinstance(key, ScalarNode) and isinstance(value, (MappingNode, ScalarNode))):
            continue

        key_value = key.value
        if key_value in result:
            if isinstance(result[key_value], MappingNode) and isinstance(value, MappingNode):
                # Recursively merge two MappingNodes
                result[key_value] = deep_merge_mapping_nodes(result[key_value], value)
            else:
                # Non-MappingNode value, the last one wins
                result[key_value] = value
        else:
            result[key_value] = value

    return [(ScalarNode(tag=key.tag, value=key.value), value) for key, value in result.items()]

def deep_merge_mapping_nodes(node1, node2):
    """
    Deep merge two MappingNodes. If there are colliding keys with non-MappingNode values,
    the value from node2 wins.
    """
    merged_value = {}
    for key, value in node1.value + node2.value:
        key_value = key.value
        if key_value in merged_value:
            if isinstance(merged_value[key_value], MappingNode) and isinstance(value, MappingNode):
                merged_value[key_value] = deep_merge_mapping_nodes(merged_value[key_value], value)
            else:
                merged_value[key_value] = value
        else:
            merged_value[key_value] = value

    return MappingNode(tag=node1.tag, value=list(merged_value.items()))

