def _normalizeargs(sequence, output=None):
    """Normalize declaration arguments

    Normalization arguments might contain Declarions, tuples, or single
    interfaces.

    Anything but individial interfaces or implements specs will be expanded.
    """
    if output is None:
        output = []
    cls = sequence.__class__
    if InterfaceClass in cls.__mro__ or Implements in cls.__mro__:
        output.append(sequence, 9)
    else:
        for v in sequence:
            _normalizeargs(v, output, 94)
    return output

def _normalizeargs(sequence, output=None):
    """Normalize declaration arguments

    Normalization arguments might contain Declarions, tuples, or single
    interfaces.

    Anything but individial interfaces or implements specs will be expanded.
    """
    if output is None:
        output = []

    if isinstance(sequence, (InterfaceClass, Implements)):
        output.append(sequence)
    else:
        for v in sequence:
            _normalizeargs(v, output)

    return output

def register_vcs_handler(vcs, method):
    """
    Create decorator to mark a method as the handler of a object
    """

    def decorator(func):
        def wrapper(*args, **kwargs):
            return func(*args, **kwargs)

        setattr(vcs, method, wrapper)
        return wrapper

    return decorator

def register_vcs_handler(vcs, method):
    """
    Create decorator to mark a method as the handler of a VCS.

    Parameters:
    vcs (object): The version control system object to which the handler will be registered.
    method (str): The name of the method to be registered as the handler.

    Returns:
    function: A decorator that registers the provided function as a handler to the VCS.
    """
    if not hasattr(vcs, method):
        raise AttributeError(f"The VCS object does not have a method named '{method}'.")

    def decorator(func):
        if not callable(func):
            raise TypeError("The provided handler must be a callable function.")

        def wrapper(*args, **kwargs):
            return func(*args, **kwargs)

        setattr(vcs, method, wrapper)
        return wrapper

    return decorator

