        if any(isinstance(key, str) and key not in self.index for key in keys):
            raise KeyError("String key not found in the index")

    if not keys:
        return list(self.index.values())

    # Use list comprehension for a more Pythonic approach
    try:
        return [self.index[key] for key in keys]
    except KeyError as e:
        raise KeyError(f"Key '{e.args[0]}' not found in the index")

