import re


def unquote(name):
    """Remove quote from the given name."""
    assert isinstance(name, (str, bytes))

    def unquoted_char(match):
        """For each ;000 return the corresponding byte."""
        char_code = match.group(0)[1:]  # Skip the semicolon
        if len(char_code) != 3:
            return match.group(0)
        try:
            return bytes([int(char_code)])
        except ValueError:
            return match.group(0)
    return re.sub(b';[0-9]{3}', unquoted_char, name)

