def _dictsum(dicts):
    """
    Combine values of the dictionaries supplied by iterable dicts.

    >>> _dictsum([{'a': 1, 'b': 2}, {'a': 5, 'b': 0}])
    {'a': 6, 'b': 2}
    """
    if not dicts:
        return {}

    combined_dict = next(iter(dicts)).copy()
    for d in dicts[1:]:
        for k, v in d.items():
            if k in combined_dict:
                combined_dict[k] += v
            else:
