import re

def detect_platform(platform='this'):
    if platform == 'this':
        import os
        if os.name == 'posix':
            platform = 1
        else:
            platform = 0
    if platform not in [0, 1]:
        raise ValueError("Invalid platform value. Must be 'this', 1 (POSIX), or 0 (Windows/CMD).")
    return platform

def split_command_line(s, platform='this'):
    """
    Multi-platform variant of shlex.split() for command-line splitting.
    For use with subprocess, for argv injection etc. Using fast REGEX.

    platform: 'this' = auto from current platform;
              1 = POSIX;
              0 = Windows/CMD
              (other values reserved)
    """
    platform = detect_platform(platform)

    # Define regex patterns for POSIX and Windows
    posix_pattern = re.compile(r'''((?:[^ "'`]+|'[^']*'|"[^"]*"|`[^`]*`)+)''')
    windows_pattern = re.compile(r'''((?:[^ "']+|'[^']*'|"[^"]*")+)''')

    if platform == 1:
        return posix_pattern.findall(s)
    elif platform == 0:
        return windows_pattern.findall(s)
    else:
        raise AssertionError("Unexpected platform value after validation.")

