        'seconds': 'seconds',
        'minutes': 'minutes',
        'hours': 'hours',
        'days': 'days',
        'weeks': 'weeks',
        'months': 'days',  # Approximate 1 month as 30 days
        'years': 'days'    # Approximate 1 year as 365 days
    }

    # Convert months and years to days
    if time_unit == 'months':
        number *= 30
    elif time_unit == 'years':
        number *= 365

    # Ensure the time unit is valid
    if time_unit not in time_units:
        raise ValueError(f"Invalid time unit '{time_unit}' in frequency '{frequency}'")

    # Construct and return the timedelta object
