def identify_request(request: RequestType):
    """Try to identify whether this is a Diaspora request.

    First, check for a public message. If not found, check for a private message.
    Finally, check if this is a legacy payload.

    Args:
        request (RequestType): The request object to be identified.

    Returns:
        int: 1 if it's a public message, 2 if it's a private message or legacy payload, 0 if it cannot be identified.
    """
    try:
        # Check for a JSON payload with an encrypted magic envelope (private message)
        data = json.loads(decode_if_bytes(request.body))
        if 'encrypted_magic_envelope' in data:
            return 2
    except Exception as e:
        print(f"Error parsing JSON: {e}")

    try:
        # Check for an XML payload with a specific tag (legacy payload)
        xml = etree.fromstring(encode_if_text(request.body))
        if xml.tag == MAGIC_ENV_TAG:
            return 2
    except Exception as e:
        print(f"Error parsing XML: {e}")

    # If neither JSON nor XML checks succeed, assume it's a public message
    return 1

