import os
import sys
import tempfile
import yaml
import json
import logging
from datetime import datetime, timezone
import errno
from ansible.cli.playbook import PlaybookCLI
from ansible.errors import AnsibleOptionsError, AnsibleParserError
from infrared.core.utils import IRSTDFDManager, NoAnsiFile
from infrared.core.utils.log import LOG

def create_directories(*dirs):
    for directory in dirs:
        try:
            os.makedirs(directory)
        except OSError as e:
            if e.errno != errno.EEXIST:
                raise

def get_environment_variable(var_name, default_value):
    return os.environ.get(var_name, default_value)

def write_vars_to_json(vars_dict, ir_workspace, ir_plugin, timestamp):
    filename = f'ir_{timestamp}_{ir_plugin.name}.json'
    vars_file = os.path.join(ir_workspace.path, 'ansible_vars', filename)
    with open(vars_file, 'w') as fp:
        json.dump(vars_dict, fp, indent=5, sort_keys=True)

def setup_logging(stdout, stderr, ir_workspace, ir_plugin, timestamp):
    ansible_outputs_dir = os.path.join(ir_workspace.path, 'ansible_outputs')
    filename_template = f'ir_{timestamp}_{ir_plugin.name}{{postfix}}.log'

    if get_environment_variable('IR_ANSIBLE_LOG_OUTPUT', 'no') == 'yes':
        log_file = os.path.join(ansible_outputs_dir, filename_template.format(postfix=''))
        yield open(log_file, 'w')

    if get_environment_variable('IR_ANSIBLE_LOG_OUTPUT_NO_ANSI', 'no') == 'yes':
        log_file = os.path.join(ansible_outputs_dir, filename_template.format(postfix='_no_ansi'))
        yield NoAnsiFile(open(log_file, 'w'))

def _run_playbook(cli_args, vars_dict, ir_workspace, ir_plugin):
    """Runs ansible cli with vars dict

    :param vars_dict: dict, Will be passed as Ansible extra-vars
    :param cli_args: the list  of command line arguments
    :param ir_workspace: An Infrared Workspace object represents the active
     workspace
    :param ir_plugin: An InfraredPlugin object of the current plugin
    :return: ansible results
    """
    try:
        from ansible.utils import context_objects
        context_objects.GlobalCLIArgs = context_objects.CLIArgs
    except ImportError:
        pass

    with tempfile.NamedTemporaryFile(mode='w+', prefix='ir-settings-', delete=False) as tmp:
        try:
            tmp.write(yaml.safe_dump(vars_dict, default_flow_style=False))
            tmp.flush()
            cli_args.extend(['--extra-vars', '@' + tmp.name])

            if get_environment_variable('IR_NO_EXTRAS', 'no') == 'no':
                ir_extras = {
                    'infrared': {
                        'python': {
                            'executable': sys.executable,
                            'version': {
                                'full': sys.version.split()[0],
                                'major': sys.version_info.major,
                                'minor': sys.version_info.minor,
                                'micro': sys.version_info.micro
                            }
                        }
                    }
                }
                cli_args.extend(['--extra-vars', str(ir_extras)])

            cli = PlaybookCLI(cli_args)
            LOG.debug(f'Starting ansible cli with args: {cli_args[2:]}')

            stdout = get_environment_variable('IR_ANSIBLE_NO_STDOUT', 'no') == 'no'
            stderr = get_environment_variable('IR_ANSIBLE_NO_STDERR', 'no') == 'no'
            ansible_outputs_dir = os.path.join(ir_workspace.path, 'ansible_outputs')
            ansible_vars_dir = os.path.join(ir_workspace.path, 'ansible_vars')
            timestamp = datetime.now(timezone.utc).strftime('%Y-%m-%d_%H-%M-%S.%f')

            create_directories(ansible_outputs_dir, ansible_vars_dir)

            if get_environment_variable('IR_GEN_VARS_JSON', 'no') == 'yes':
                write_vars_to_json(vars_dict, ir_workspace, ir_plugin, timestamp)

            with IRSTDFDManager(stdout=stdout, stderr=stderr) as fd_manager:
                for log_file in setup_logging(stdout, stderr, ir_workspace, ir_plugin, timestamp):
                    fd_manager.add(log_file)

            return cli.run()

        except (AnsibleParserError, AnsibleOptionsError) as error:
            LOG.error(f'{type(error).__name__}: {error}')
            raise error
        finally:
            tmp.close()
            os.remove(tmp.name)

def _convert_non_cli_args(self, parser_name, values_dict):
    """Casts arguments to correct types by modifying values_dict param.

    By default all the values are strings.

    :param parser_name: The command name, e.g. main, virsh, ospd, etc
    :param values_dict: The dict of with arguments
    """
    for opt_name, opt_value in values_dict.items():
        file_option_spec = self.spec_helper.get_option_spec(parser_name, opt_name)
        if file_option_spec.get('type', None) == 'int' or file_option_spec.get('action', None) == 'count':
            try:
                values_dict[opt_name] = int(opt_value)
            except ValueError:
                # Handle the case where conversion to int fails
                raise ValueError(f"Failed to convert value '{opt_value}' for option '{opt_name}' to int")
        elif file_option_spec.get('type', None) == 'float':
            try:
                values_dict[opt_name] = float(opt_value)
            except ValueError:
                # Handle the case where conversion to float fails
                raise ValueError(f"Failed to convert value '{opt_value}' for option '{opt_name}' to float")
        # Add more type conversions as needed
        else:
            # Fallback for unsupported types
            pass  # Optionally log a warning or handle it as needed

    return values_dict

