import re
from typing import Tuple, Set, Callable

def find_tags(text: str, replacer: Callable[[str], str] = None) -> Tuple[Set[str], str]:
    if replacer is None:
        replacer = lambda x: x

    tags = set()
    code_block = False
    replaced_text = ""
    current_word = ""

    # Regex to match words
    word_pattern = re.compile(r'[a-zA-Z0-9_]+')

    for line in text.splitlines():
        for part in word_pattern.split(line):
            if '`' in part:
                parts = part.split('`')
                for i, p in enumerate(parts):
                    if i % 2 == 0:
                        # Not in a code block
                        for word in word_pattern.findall(p):
                            tags.add(word)
                            replaced_text += replacer(word)
                    else:
                        # In a code block
                        replaced_text += '`' + p + '`'
            else:
                # No code block in this part
                for word in word_pattern.findall(part):
                    tags.add(word)
                    replaced_text += replacer(word)
            replaced_text += part.replace(word, '')
        replaced_text += '\n'

    return tags, replaced_text.strip()

