def get_nested_custom_and_control_args(self, args):
    # Initialize parsers for different types of arguments
    parser = argparse.ArgumentParser(add_help=False)
    control_group = parser.add_argument_group('control arguments')
    nested_group = parser.add_argument_group('nested arguments')

    # Add a placeholder for all possible arguments to capture them
    control_group.add_argument('--', dest='control_args', action='append', nargs='*')
    nested_group.add_argument('-', dest='nested_args', action='append', nargs='*')

    # Parse the arguments
    parsed_args, unknown_args = parser.parse_known_args(args)

    control_args = {}
    nested_args = {}

    # Process control arguments
    if parsed_args.control_args:
        for arg_list in parsed_args.control_args:
            for arg in arg_list:
                if arg.startswith("--"):
                    arg = arg[2:]
                elif arg.startswith("-"):
                    arg = arg[1:]
                if "=" in arg:
                    key, value = arg.split("=", 1)
                    control_args[key] = value
                else:
                    control_args[arg] = True

    # Process nested arguments
    if parsed_args.nested_args:
        for arg_list in parsed_args.nested_args:
