def merge_extra_vars(vars_dict, extra_vars=None):
    """Extend ``vars_dict`` with ``extra-vars``

    :param vars_dict: Dictionary to merge extra-vars into
    :param extra_vars: List of extra-vars
    """
    if extra_vars is None:
        extra_vars = []

    for extra_var in extra_vars:
        if extra_var.startswith('@'):
            # Handle file loading for extra_var starting with '@'
            try:
                with open(extra_var[1:], 'r') as f_obj:
                    loaded_yml = yaml.safe_load(f_obj)
                dict_utils.dict_merge(
                    vars_dict,
                    loaded_yml,
                    conflict_resolver=dict_utils.ConflictResolver.unique_append_list_resolver
                )
            except FileNotFoundError:
                raise exceptions.IRExtraVarsException(f"File not found: {extra_var[1:]}")
            except Exception as e:
                raise exceptions.IRExtraVarsException(f"Error loading file {extra_var[1:]}: {e}")

        else:
            if '=' not in extra_var:
                raise exceptions.IRExtraVarsException(f"Invalid extra-var format: {extra_var}")
            key, value = extra_var.split("=", 1)
            if value.startswith('@'):
                # Handle file loading for value starting with '@'
                try:
                    with open(value[1:], 'r') as f_obj:
                        loaded_yml = yaml.safe_load(f_obj)
                    tmp_dict = {}
                    dict_utils.dict_insert(tmp_dict, loaded_yml, *key.split("."))
                    dict_utils.dict_merge(
                        vars_dict,
                        tmp_dict,
                        conflict_resolver=dict_utils.ConflictResolver.unique_append_list_resolver
                    )
                except FileNotFoundError:
                    raise exceptions.IRExtraVarsException(f"File not found: {value[1:]}")
                except Exception as e:
                    raise exceptions.IRExtraVarsException(f"Error loading file {value[1:]}: {e}")

            else:
                # Directly insert key-value pair into vars_dict
                dict_utils.dict_insert(vars_dict, value, *key.split("."))

    return vars_dict

