        for arg, value in deprecated_answer_file_args.items():
            warning(f"Deprecated answer file argument '{arg}' with value '{value}' is used.")
            if LOG:
                LOG.warning(f"Deprecated answer file argument '{arg}' with value '{value}' is used.")

        # Raise exception for critical deprecations
