def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    if not bases:
        raise TypeError("At least one base class is required")

    # Initialize the list of metaclasses found in the bases
    metaclasses = []

    # Collect metaclasses from the bases
    for base in bases:
        base_metaclass = getattr(base, '__class__', type(base))
        if base_metaclass not in metaclasses:
            metaclasses.append(base_metaclass)

    # If an explicit metaclass is provided, use it
    if explicit_mc is not None:
        metaclasses.insert(0, explicit_mc)

    # Determine the most derived metaclass
    if len(metaclasses) == 1:
        return metaclasses[0]
    elif len(metaclasses) > 1:
        # Find the minimal set of bases that can be used to determine the metaclass
        minimal_bases = minimalBases(metaclasses)
        if len(minimal_bases) == 1:
            return minimal_bases[0]
        else:
            raise TypeError("Conflicting metaclasses found: {}".format(minimal_bases))
    else:
        raise TypeError("No valid metaclass found")

