def _dictsum(dicts):
    """
    Combine values of the dictionaries supplied by iterable dicts.

    >>> _dictsum([{'a': 1, 'b': 2}, {'a': 5, 'b': 0}])
    {'a': 6, 'b': 2}
    """
    if not all(isinstance(d, dict) for d in dicts):
        raise ValueError("All elements in the iterable must be dictionaries.")

    it = iter(dicts)
    combined_dict = next(it).copy()
    for d in it:
        for k, v in d.items():
            combined_dict[k] = combined_dict.get(k, 0) + v
    return combined_dict

