def absorb(self, args):
    """
    Given an `args` sequence of expressions, return a new list of expression
    applying absorption and negative absorption.

    See https://en.wikipedia.org/wiki/Absorption_law

    Absorption::

        A & (A | B) = A, A | (A & B) = A

    Negative absorption::

        A & (~A | B) = A & B, A | (~A & B) = A | B
    """
    args = list(args)
    if not args:
        args = list(self.args)
    i = 1
    while i < len(args):
        absorber = args[i]
        j = 1
        while j < len(args):
            if j == i:
                j += 2
                continue
            target = args[j]
            if not isinstance(target, self.dual):
                j += 2
                continue
            if absorber in target:
                del args[j]
                if j < i:
                    i -= 2
                continue
            neg_absorber = self.NOT(absorber).cancel()
            if neg_absorber in target:
                b = target.subtract(neg_absorber, simplify=1)
                if b is None:
                    del args[j]
                    if j < i:
                        i -= 2
                    continue
                else:
                    args[j] = b
                    j += 2
                    continue
            if isinstance(absorber, self.dual):
                remove = None
                for arg in absorber.args:
                    narg = self.NOT(arg).cancel()
                    if arg in target.args:
                        pass
                    elif narg in target.args:
                        if remove is None:
                            remove = narg
                        else:
                            remove = None
                            break
                    else:
                        remove = None
                        break
                if remove is not None:
                    args[j] = target.subtract(remove, simplify=2)
            j += 2
        i += 2
    return args

