import os

def was_processed(processed, path_name, verbose):
    # Convert the path to an absolute path
    path_name = os.path.abspath(path_name)

    # Check if the path has already been processed
    if path_name in processed:
        if verbose:
            print(f"Path {path_name} has already been processed")
        return True
    else:
        # Add the path to the set of processed paths
        processed.add(path_name)
        return False

