import re

def unquote(name):
    """Remove quote from the given name."""
    assert isinstance(name, str), "name must be a string"

    def unquoted_char(match):
        """For each ;000 return the corresponding byte."""
        match_str = match.group(0)
        if len(match_str) != 4:
            return match_str
        try:
            return bytes([int(match_str[1:], 8)])
        except ValueError:
            return match_str
    return re.sub(r';\d{3}', unquoted_char, name)

# Example usage
print(unquote("Hello;040World"))  # Should print: Hello World

