import re

def split(s, platform='this'):
    if platform == 'this':
        import os
        if os.name == 'posix':
            platform = 1
        elif os.name == 'nt':
            platform = 0
        else:
            raise ValueError("Unsupported platform.")

    if platform == 1:
        # POSIX-style splitting
        return re.findall(r'([^\s"]+|"[^"]*")', s)
    elif platform == 0:
        # Windows-style splitting
        return re.findall(r'([^\s"]+|"[^"]*")', s.replace('/', '\\'))
    else:
        raise ValueError("Invalid platform value. Must be 'this', 1, or 0.")

