import datetime


def parse_frequency(frequency):
    """
    Given a frequency string with a number and a unit of time, return a corresponding
    datetime.timedelta instance or None if the frequency is None or "always".

    For instance, given "3 weeks", return datetime.timedelta(weeks=3)

    Raise ValueError if the given frequency cannot be parsed.
    """
    if not frequency or frequency.lower() == 'always':
        return None
    try:
        number, time_unit = frequency.strip().lower().split(' ')
        number = int(number)
    except ValueError:
        raise ValueError(f"Could not parse consistency check frequency '{frequency}'")

    # Adjust for units that are not directly supported by timedelta
    if time_unit == 'month':
        time_unit = 'months'
    if time_unit == 'year':
        time_unit = 'years'

    if time_unit.endswith('s'):
        time_unit = time_unit[:-1]  # Remove 's' to match timedelta parameter names

    if time_unit == 'month':
        number *= 4  # Approximate a month as 4 weeks
        time_unit = 'weeks'
    elif time_unit == 'year':
        number *= 365  # Approximate a year as 365 days
        time_unit = 'days'

    try:
        return datetime.timedelta(**{time_unit + 's': number})
    except TypeError:
        raise ValueError(f"Could not parse consistency check frequency '{frequency}'")

