import re

class ValidationError(Exception):
    def __init__(self, messages: List[Dict[str, str]]):
        super().__init__(messages)
        self.messages = messages

def _validate_labels(labels: Dict[str, str]) -> None:
    """
    Check that keys and values in the given labels match against their corresponding
    regular expressions.

    Args:
        labels (dict): the different labels to validate.

    Raises:
        ValidationError: if any of the keys and labels does not match their respective
            regular expression. The error contains as message the list of all errors
            which occurred in the labels. Each element of the list is a dictionary with
            one key-value pair:
            - key: the label key or label value for which an error occurred as string.
            - value: the error message.
    """

    def validate_key(key: str) -> bool:
        """Validate the label key using a regular expression."""
        if not isinstance(key, str):
            raise TypeError('expected string or bytes-like object')
        return re.match(r'^[a-zA-Z0-9_]+$', key) is not None

    def validate_value(value: str) -> bool:
        """Validate the label value using a regular expression."""
        if not isinstance(value, str):
            raise TypeError('expected string or bytes-like object')
        return re.match(r'^[a-zA-Z0-9_]+$', value) is not None

    errors = []
    for key, value in labels.items():
        try:
            if not validate_key(str(key)):
                errors.append({str(key): f"Label key '{key}' does not match the regex ^[a-zA-Z0-9_]+$"})
        except TypeError as e:
            errors.append({str(key): str(e)})

        try:
            if not validate_value(str(value)):
                errors.append({str(value): f"Label value '{value}' does not match the regex ^[a-zA-Z0-9_]+$"})
        except TypeError as e:
            errors.append({str(value): str(e)})

    if errors:
        raise ValidationError(errors)

