    number = None
    sup = None

    if issue:
        parts = issue.split()
        for part in parts:
            stripped_part = part.strip().lower()
            if stripped_part.isdigit():
                number = int(stripped_part)
            elif stripped_part.endswith('sup'):
                sup = stripped_part.replace('sup', '')

    return number, sup

