        RE_CMD_LEX = re.compile(
            r'"((?:""|\\["\\]|[^"])*)"?()|(\\\\(?=\\*")|\\")|(&&?|\|\|?|\d?>|[<])|([^\\s"&|<>]+)|(\s+)|(.)'
        )
