def _include_groups(parser_dict):
    """
    Resolves the include dict directive in the spec files.

    Parameters:
    parser_dict (dict): The dictionary containing the specification with possible 'include' directives.

    Returns:
    dict: The updated dictionary with included groups resolved.
    """
    if 'include' in parser_dict:
        include_dict = parser_dict.pop('include')  # Remove 'include' from parser_dict to avoid infinite recursion
        for key, value in include_dict.items():
            if key in parser_dict:
                parser_dict[key].update(value)
            else:
                parser_dict[key] = value
    return parser_dict

