    missing_args = []

    def evaluate_condition(condition_str, args_dict):
        # Split the condition string into tokens
        tokens = condition_str.split()
        evaluated_tokens = []

        for token in tokens:
            if token in opts_names:
                value = args_dict.get(command_name, {}).get(token.strip())
                evaluated_tokens.append(str(value) if value is not None else 'None')
            else:
                evaluated_tokens.append(token)

        # Join the evaluated tokens back into a string
        evaluated_condition = ' '.join(evaluated_tokens)

        # Safely evaluate the condition using a restricted environment
        try:
            return eval(evaluated_condition, {"__builtins__": {}}, {})
        except Exception as e:
            print(f"Error evaluating condition: {e}")
            return False

    for option_spec in options_spec:
        if 'required_when' in option_spec:
            req_when_args = option_spec['required_when']
            if not isinstance(req_when_args, list):
                req_when_args = [req_when_args]

            option_results = []
            for req_when_arg in req_when_args:
                if evaluate_condition(req_when_arg, args):
                    option_results.append(True)
                else:
                    option_results.append(False)

            if all(option_results) or self.spec_helper.get_option_state(
                command_name, option_spec['name'], args) == helper.OptionState['NOT_SET']:
                missing_args.append(option_spec['name'])

    return missing_args

