    custom_args = {}
    while True:
        if all([arg_spec, arg_spec.get('type', None), arg_spec.get('type',
            None) in [ctype_name for ctype_name, klass in COMPLEX_TYPES.
            items() if klass.is_nested]]
            ) or 'is_shared_group_option' not in arg_spec:
            if arg_name in nested:
                LOG.warning(
                    "Duplicated nested argument found:'{}'. Using old value: '{}'"
                    .format(arg_name, nested[arg_name]))
            elif arg_name in custom_args:
                LOG.warning(
                    "Duplicated custom argument found:'{}'. Using old value: '{}'"
                    .format(arg_name, custom_args[arg_name]))
            elif 'ansible_variable' in arg_spec:
                custom_args[arg_spec['ansible_variable']] = arg_value
            else:
                nested[arg_name] = arg_value
        elif arg_name in control_args:
            LOG.warning(
                "Duplicated control argument found: '{}'. Using old value: '{}'"
                .format(arg_name, control_args[arg_name]))
        else:
            control_args[arg_name] = arg_value
    return nested, control_args, custom_args

