def format(
    self,
    sql: AnyStr,
    params: Union[Dict[Union[str, int], Any], Sequence[Any]],
) -> Tuple[AnyStr, Union[Dict[Union[str, int], Any], Sequence[Any]]]:
    """
    Convert SQL query parameters from in-style to out-style format.

    Args:
        sql: The SQL query string or bytes
        params: Parameters as either a mapping or sequence

    Returns:
        Tuple of (formatted SQL, converted parameters)
    """
    # Validate input types
    if not isinstance(sql, (str, bytes)):
        raise TypeError("sql must be str or bytes")

    # Handle empty parameters
    if params is None:
        params = {}

    # Convert parameters based on their type
    if isinstance(params, (dict, Mapping)):
        # Handle named parameters
        converted_params = {}
        for key, value in params.items():
            if not isinstance(key, (str, int)):
                raise TypeError("Parameter keys must be str or int")
            converted_params[str(key)] = value

    elif isinstance(params, Sequence):
        # Handle ordinal parameters
        converted_params = list(params)

    else:
        raise TypeError("params must be a mapping or sequence")

    # Keep sql in same format as input (str or bytes)
    if isinstance(sql, bytes):
        formatted_sql = sql
    else:
        formatted_sql = sql

    return formatted_sql, converted_params

