def validate_choices_args(self, args):
    """Check if value of choice arguments is one of the available choices.

    :param args: The received arguments.
    :raises IRInvalidChoiceException: when argument value isn't in allowed choices
    """
    invalid_options = []

    # Handle empty args
    if not args:
        return

    for parser_name, parser_dict in args.items():
        # Skip if parser dict is empty
        if not parser_dict:
            continue

        for spec_option in self.spec_helper.get_parser_option_specs(parser_name):
            if 'choices' not in spec_option:
                continue

            option_name = spec_option['name']
            if option_name not in parser_dict:
                continue

            # resolve choices
            choices = spec_option['choices']
            option_value = parser_dict[option_name]

            # Handle case where choices is empty or None
            if not choices:
                continue

            if option_value not in choices:
                invalid_options.append((
                    option_name,
                    option_value,
                    choices
                ))

    # Raise exception if any invalid options found
    if invalid_options:
        raise IRInvalidChoiceException(
            "The following arguments don't match their choices: {}".format(
                ', '.join([
                    "'{}' = '{}' not in {}".format(name, value, choices)
                    for name, value, choices in invalid_options
                ])
            )
        )

