    """
    Extend ``vars_dict`` with ``extra-vars``

    :param vars_dict: Dictionary to merge extra-vars into
    :param extra_vars: List of extra-vars
    :return: Merged dictionary
    """
    if extra_vars is None:
        extra_vars = []

    for var in extra_vars:
        if isinstance(var, str) and var.startswith('@'):
            # Handle YAML file paths
            try:
                with open(var[1:], 'r') as f:
                    from yaml import safe_load
                    yaml_content = safe_load(f)
                    if yaml_content:
                        vars_dict.update(yaml_content)
            except Exception as e:
                raise IRExtraVarsException(
                    "Error loading extra-vars from file '{}': {}".format(
                        var[1:], str(e)))
        elif isinstance(var, dict):
            # Handle dictionary inputs
            vars_dict.update(var)
        else:
            raise IRExtraVarsException(
                "Extra-vars must be either a dictionary or a YAML file path starting with '@'"
            )

    return vars_dict

