def extostr(cls, e, max_level=30, max_path_level=5):
    try:
        # Get the full exception info
        if not e:
            return "None"

        # Build the exception chain
        exc_list = []
        current = e
        level = 0

        while current and level < max_level:
            exc_list.append(current)
            current = getattr(current, '__cause__', None) or getattr(current, '__context__', None)
            level += 1

        # Format each exception in the chain
        formatted = []
        for exc in exc_list:
            # Get traceback
            tb = getattr(exc, '__traceback__', None)
            if tb:
                tb_list = traceback.extract_tb(tb)
                # Limit the traceback based on max_path_level
                if max_path_level > 0:
                    tb_list = tb_list[-max_path_level:]
                tb_str = ''.join(traceback.format_list(tb_list))
            else:
                tb_str = ''

            # Format the exception
            exc_str = f"{exc.__class__.__name__}: {str(exc)}"
            if tb_str:
                formatted.append(f"Traceback (most recent call last):\n{tb_str}{exc_str}")
            else:
                formatted.append(exc_str)

        return "\n\nCaused by: ".join(formatted)

    except Exception as format_ex:
        # Fallback to basic formatting if something goes wrong
        return f"Error formatting exception: {str(e)} (Format error: {str(format_ex)})"

