def validate_as_prior_version(self, prior):
    # Get logical path maps for both versions
    this_map = self.get_logical_path_map()
    prior_map = prior.get_logical_path_map()

    # Check that all items in prior version exist in current version
    if not set(prior_map.keys()).issubset(set(this_map.keys())):
        missing = set(prior_map.keys()) - set(this_map.keys())
        return self.error("invalid_state", 
                         f"Prior version contains paths not in current version: {', '.join(missing)}")

    # Compare states between versions
    for path in prior_map.keys():
        if not self.compare_states_for_version(prior, path):
            return self.error("invalid_state",
                            f"State mismatch for path: {path}")

    return True

