def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    # Get all nodes that appear as subjects (children)
    all_nodes = {s for s, o in graph.subject_objects(prop)}

    # Get all nodes that appear as objects (parents)
    non_roots = {o for s, o in graph.subject_objects(prop)}

    # If roots parameter is provided, restrict the search to those nodes
    if roots is not None:
        all_nodes &= roots

    # Return nodes that appear as subjects but not as objects (roots)
    return all_nodes - non_roots

