import subprocess
import logging

def ansible_playbook(ir_workspace, ir_plugin, playbook_path, verbose=None, extra_vars=None, ansible_args=None):
    """Wraps the 'ansible-playbook' CLI.

    :param ir_workspace: An Infrared Workspace object represents the active workspace
    :param ir_plugin: An InfraredPlugin object of the current plugin
    :param playbook_path: the playbook to invoke
    :param verbose: Ansible verbosity level
    :param extra_vars: dict. Passed to Ansible as extra-vars
    :param ansible_args: dict of ansible-playbook arguments to plumb down directly to Ansible
    """
    LOG = logging.getLogger("main")
    cmd = ["ansible-playbook"]

    # Handle verbosity level
    if verbose is not None:
        try:
            v_level = int(verbose)
            cmd.append('-' + 'v' * v_level)
        except (ValueError, TypeError):
            LOG.error(f"Invalid verbose level: {verbose}")
            raise ValueError(f"Verbose level must be a number, got: {verbose}")

    # Handle extra variables
    if extra_vars is not None:
        if isinstance(extra_vars, dict):
            # Convert dict to ansible-playbook format
            extra_vars_str = ' '.join([f"{k}={v}" for k, v in extra_vars.items()])
            cmd.extend(["--extra-vars", extra_vars_str])
        else:
            LOG.error(f"Invalid extra_vars format: {extra_vars}")
            raise TypeError("extra_vars must be a dictionary")

    # Handle additional ansible arguments
    if ansible_args is not None:
        if isinstance(ansible_args, dict):
            for key, value in ansible_args.items():
                if value is True:
                    cmd.append(f"--{key}")
                else:
                    cmd.append(f"--{key}={value}")
        else:
            LOG.error(f"Invalid ansible_args format: {ansible_args}")
            raise TypeError("ansible_args must be a dictionary")

    # Add playbook path
    if not playbook_path:
        LOG.error("Playbook path is required")
        raise ValueError("Playbook path cannot be empty")
    cmd.append(playbook_path)

    # Debug log the final command
    LOG.debug(f"Executing ansible command: {' '.join(cmd)}")

    try:
        return subprocess.run(cmd, check=True)
    except subprocess.CalledProcessError as e:
        LOG.error(f"Ansible playbook execution failed: {e}")
        raise

