def get_deprecated_args(self):
    deprecated_map = defaultdict(list)
    for option_tuple in self.spec_helper.iterate_option_specs():
        # Assuming the tuple contains (name, deprecated_args) based on the context
        name, deprecated_args = option_tuple
        if deprecated_args:  # Only add if there are deprecated arguments
            deprecated_map[name] = deprecated_args
    return dict(deprecated_map)

def validate_arg_deprecation(self, cli_args, answer_file_args):
    """
    Validates and prints the deprecated arguments.

    :param cli_args: the dict of arguments from cli
    :param answer_file_args: the dict of arguments from files
    """
    # Get list of deprecated arguments
    deprecated_args = self.get_deprecated_args()

    # Check CLI arguments for deprecation
    for arg_name, arg_value in cli_args.items():
        if arg_name in deprecated_args:
            LOG.warning(
                "Argument '{}' is deprecated. {}".format(
                    arg_name, 
                    deprecated_args[arg_name]
                )
            )

    # Check answer file arguments for deprecation
    for arg_name, arg_value in answer_file_args.items():
        if arg_name in deprecated_args:
            LOG.warning(
                "Argument '{}' from answer file is deprecated. {}".format(
                    arg_name,
                    deprecated_args[arg_name]
                )
            )

    # Raise exception if deprecated argument would cause failure
    for arg_name in deprecated_args:
        if arg_name in cli_args or arg_name in answer_file_args:
            if deprecated_args[arg_name].startswith('This argument will be removed'):
                raise IRDeprecationException(
                    "Deprecated argument '{}' was used. {}".format(
                        arg_name,
                        deprecated_args[arg_name]
                    )
                )

